mapper.select:

select user_email userEmail
from users
where user_email = #{userEmail}

mapper.update:

update users
set user_password = #{userPassword}
    ,user_name = #{userName}
    ,user_birth_year = #{userBirthYear}
    ,user_sex = #{userSex}
where user_email = #{userEmail}

update users
set session_id= #{sessionId}
    ,limit_date= #{limitDate}
where user_email= #{userEmail}
